$(document).ready(function(){$("a[href]:not(.delete, .delete-confirm, .target)").live("click",function(a){a.preventDefault();window.location=$(this).attr("href")});$("#newtask").submit(function(){$.post("/",$(this).serialize(),function(a){$("tbody").append(a);$("#newtask").each(function(){this.reset()})},"text");return!1});$("#newtask .input").keypress(function(a){if(a.which===13){a.preventDefault();$("form#login").submit();return!1}});$("a.delete").live("click",function(a){$(this).siblings(".deleteModal").modal()});$("a.delete-confirm").live("click",function(a){var b=this;a.preventDefault();$.post(b.href,{_method:"delete"},function(a){window.location.pathname=="/edit"?$(b).closest("tr").remove():window.location.pathname="/edit"},"script")});$("a.target").live("click",function(a){var b=this;a.preventDefault();$.post(b.href,null,function(){$(b).children("img").toggleClass("complete")})});$("button.forgot").click(function(a){a.preventDefault();$("form").attr("action","/forgot").submit()})});